<?php global $tour_obj, $bookyourtravel_theme_globals, $current_user, $enable_extra_items, $booking_form_fields, $bookyourtravel_theme_of_custom;$price_decimal_places = $bookyourtravel_theme_globals->get_price_decimal_places();$default_currency_symbol = $bookyourtravel_theme_globals->get_default_currency_symbol();$show_currency_symbol_after = $bookyourtravel_theme_globals->show_currency_symbol_after();$enc_key = $bookyourtravel_theme_globals->get_enc_key();$add_captcha_to_forms = $bookyourtravel_theme_globals->add_captcha_to_forms();$c_val_1_tour = mt_rand(1, 20);$c_val_2_tour = mt_rand(1, 20);$c_val_1_tour_str = BookYourTravel_Theme_Utils::encrypt($c_val_1_tour, $enc_key);$c_val_2_tour_str = BookYourTravel_Theme_Utils::encrypt($c_val_2_tour, $enc_key);$booking_form_fields = $bookyourtravel_theme_globals->get_booking_form_fields();if (!isset($current_user))	$current_user = wp_get_current_user();$user_info = get_userdata($current_user->ID);?><script>	window.currencySymbol = <?php echo json_encode($default_currency_symbol); ?>;	window.priceDecimalPlaces = <?php echo json_encode($price_decimal_places); ?>;	window.currencySymbolShowAfter = <?php echo json_encode($show_currency_symbol_after); ?>;	window.InvalidCaptchaMessage = <?php echo json_encode(esc_html__('Invalid captcha, please try again!', 'bookyourtravel')); ?>;	window.bookingFormDatesError = <?php echo json_encode(esc_html__('Please select booking dates', 'bookyourtravel')); ?>;			window.bookingFormRequiredError = <?php echo json_encode(esc_html__('This is a required field', 'bookyourtravel')); ?>;	window.bookingFormEmailError = <?php echo json_encode(esc_html__('You have not entered a valid email', 'bookyourtravel')); ?>;	window.bookingFormFields = <?php echo json_encode($booking_form_fields)?>;</script><?php do_action( 'bookyourtravel_show_tour_booking_form_before' ); ?><form id="tour-booking-form" method="post" action="<?php echo BookYourTravel_Theme_Utils::get_current_page_url(); ?>" class="booking tour-booking-form" style="display:none">	<fieldset>		<h3><span>01 </span><?php esc_html_e('Booking details', 'bookyourtravel') ?></h3>		<div class="row">					<div class="output one-half">				<p><?php esc_html_e('Tour name', 'bookyourtravel') ?>: 					<strong class="booking_form_tour_name_p"></strong>				</p>			</div>			<div class="output one-half">				<p><?php esc_html_e('Tour date', 'bookyourtravel') ?>: 					<strong class="booking_form_tour_date_p"></strong>				</p>			</div>					<div class="output one-half">				<p><?php esc_html_e('Adults', 'bookyourtravel') ?>: 					<strong class="booking_form_adults_p"></strong>				</p>			</div>			<div class="output one-half">				<p><?php esc_html_e('Children', 'bookyourtravel') ?>: 					<strong class="booking_form_children_p"></strong>				</p>			</div>			<?php if ($enable_extra_items) { ?>			<div class="output one-half">				<p><?php esc_html_e('Reservation total', 'bookyourtravel') ?>: 					<strong class="booking_form_reservation_total_p"></strong>				</p>			</div>			<div class="output one-half">				<p><?php esc_html_e('Extra items total', 'bookyourtravel') ?>: 					<strong class="booking_form_extra_items_total_p"></strong>				</p>			</div>			<?php } ?>			<div class="output full-width">				<p><?php esc_html_e('Total', 'bookyourtravel') ?>: 					<strong class="booking_form_total_p"></strong>				</p>			</div>		</div>			<h3><span>02 </span><?php esc_html_e('Submit tour booking', 'bookyourtravel') ?></h3>		<div class="error" style="display:none;"><div><p></p></div></div>						<?php 				foreach ($booking_form_fields as $booking_field) { 					$field_type = $booking_field['type'];			$field_hidden = isset($booking_field['hide']) && $booking_field['hide'] == 1 ? true : false;			$field_id = $booking_field['id'];			$field_label = isset($booking_field['label']) ? $booking_field['label'] : '';			$field_label = $bookyourtravel_theme_of_custom->get_translated_dynamic_string($bookyourtravel_theme_of_custom->get_option_id_context('booking_form_fields') . ' ' . $field_label, $field_label);						$field_value = isset($user_info->{$field_id}) ? $user_info->{$field_id} : '';			if (empty($field_value)) {				$field_value = isset($user_info->{'billing_' . $field_id}) ? $user_info->{'billing_' . $field_id} : '';			}			if (empty($field_value)) { 				if ($field_id == 'zip') {					$field_value = isset($user_info->{'billing_postcode'}) ? $user_info->{'billing_postcode'} : '';				} elseif ($field_id == 'town') {					$field_value = isset($user_info->{'billing_city'}) ? $user_info->{'billing_city'} : '';				} elseif ($field_id == 'address') {					$field_value = isset($user_info->{'billing_address_1'}) ? $user_info->{'billing_address_1'} : '';				}			}						if (!$field_hidden) {				if ($field_type == 'email') { ?>			<div class="row">				<div class="f-item one-half">					<label for="<?php echo esc_attr($field_id) ?>"><?php echo esc_html($field_label); ?></label>					<input value="<?php echo esc_attr($field_value); ?>" <?php echo isset($booking_field['required']) && $booking_field['required'] == '1' ? 'data-required' : ''; ?> type="email" id="<?php echo esc_attr($field_id) ?>" name="<?php echo esc_attr($field_id) ?>" />				</div>			</div>			<?php } else if ($field_type == 'textarea') { ?>			<div class="row">				<div class="f-item full-width">					<label><?php echo esc_html($field_label); ?></label>					<textarea <?php echo isset($booking_field['required']) && $booking_field['required'] == '1' ? 'data-required' : ''; ?> name='<?php echo esc_attr($field_id) ?>' id='<?php echo esc_attr($field_id) ?>' rows="10" cols="10" ><?php echo esc_html($field_value); ?></textarea>				</div>				</div>			<?php } else { ?>			<div class="row">				<div class="f-item one-half">					<label for="<?php echo esc_attr($field_id) ?>"><?php echo esc_html($field_label); ?></label>					<input value="<?php echo esc_attr($field_value); ?>" <?php echo isset($booking_field['required']) && $booking_field['required'] == '1' ? 'data-required' : ''; ?> type="text" name="<?php echo esc_attr($field_id) ?>" id="<?php echo esc_attr($field_id) ?>" />				</div>			</div>			<?php 					}			}		}		?>					<?php if ($add_captcha_to_forms) { ?>		<div class="row captcha">			<div class="f-item full-width">				<label><?php echo sprintf(esc_html__('How much is %d + %d', 'bookyourtravel'), $c_val_1_tour, $c_val_2_tour) ?>?</label>				<input type="text" required="required" id="c_val_s_tour" name="c_val_s_tour" />				<input type="hidden" name="c_val_1_tour" id="c_val_1_tour" value="<?php echo esc_attr($c_val_1_tour_str); ?>" />				<input type="hidden" name="c_val_2_tour" id="c_val_2_tour" value="<?php echo esc_attr($c_val_2_tour_str); ?>" />			</div>		</div>		<?php } ?>		<input type="hidden" name="tour_schedule_id" id="tour_schedule_id" />		<div class="booking-commands">					<?php BookYourTravel_Theme_Utils::render_link_button("#", "gradient-button cancel-tour-booking", "cancel-tour-booking", esc_html__('Cancel', 'bookyourtravel')); ?>			<?php BookYourTravel_Theme_Utils::render_submit_button("gradient-button", "submit-tour-booking", esc_html__('Submit booking', 'bookyourtravel')); ?>		</div>	</fieldset></form><div class="loading" id="wait_loading" style="display:none">	<div class="ball"></div>	<div class="ball1"></div></div><?php do_action( 'bookyourtravel_show_tour_booking_form_after' );